import Checkbox from '@material-ui/core/Checkbox';
import Icon from '@material-ui/core/Icon';
import IconButton from '@material-ui/core/IconButton';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import MenuItem from '@material-ui/core/MenuItem';
import React, { useState } from 'react';
import ToolbarMenu from './ToolbarMenu';

function LabelsMenu(props) {
	const [anchorEl, setAnchorEl] = useState(null);

	function handleMenuOpen(event) {
		setAnchorEl(event.currentTarget);
	}

	function handleMenuClose() {
		setAnchorEl(null);
	}

	console.log(props.labels);
	console.log(props.idLabels);
	return (
		<div>
			<IconButton color="inherit" onClick={handleMenuOpen}>
				<Icon>label</Icon>
			</IconButton>
			<ToolbarMenu state={anchorEl} onClose={handleMenuClose}>
				<div className="">
					{props.labels.map(label => {
						return (
							<MenuItem
								className="px-8"
								key={label.id}
								onClick={ev => {
									props.onToggleLabel(label.id);
								}}
							>
								<Checkbox checked={props.labels.includes(label.id)} />
								<ListItemText className="mx-8">{label.name}</ListItemText>
								<ListItemIcon className="min-w-24">
									<Icon>label</Icon>
								</ListItemIcon>
							</MenuItem>
						);
					})}
				</div>
			</ToolbarMenu>
		</div>
	);
}

export default LabelsMenu;
