}

export function removeCard(boardId, cardId) {
	return dispatch => {
		const request = axios.post('/api/scrumboard-app/card/remove', {
			boardId,
			cardId
		});

		return request.then(response =>
			dispatch({
				type: REMOVE_CARD,
				boardId,
				cardId
			})
		);
	};
}
