        //     }
        // });

    };
}

export function newCard(boardId, listId, cardTitle) {
    const data = new CardModel({ name: cardTitle });

    console.log(data);
    return dispatch =>
        firebaseService.db.ref(`board/${boardId}/lists/${listId}/cards`).push(data).then((snapshot) => {
            dispatch(getBoard({ boardId: boardId }));
        });


